    unsigned char key[AES_KEYLEN_256];
    RAND_bytes(key, AES_KEYLEN_256);

    // Initialize AES encryption context
    AES_KEY aesKey;
    AES_set_encrypt_key(key, 256, &aesKey);

    // Open the file in read mode
    std::ifstream inputFile(filePath, std::ios::binary);
    if (!inputFile.is_open()) {
        std::cerr << "Error opening file: " << filePath << std::endl;
        return;
    }

    // Read the file contents into a buffer
    std::vector<unsigned char> buffer((std::streamsize)inputFile.tellg());
    inputFile.seekg(0, std::ios::beg);
    inputFile.read((char*)buffer.data(), buffer.size());

    // Encrypt the file contents
    unsigned char* encryptedBuffer = new unsigned char[buffer.size()];
    for (size_t i = 0; i < buffer.size(); i++) {
        unsigned char block[AES_BLOCK_SIZE];
        memcpy(block, buffer.data() + i, AES_BLOCK_SIZE);
        AES_encrypt(block, encryptedBuffer + i * AES_BLOCK_SIZE, &aesKey);
    }

    // Write the encrypted buffer to a temporary file
    std::ofstream tempFile("temp.bin", std::ios::binary);
    if (!tempFile.is_open()) {
        std::cerr << "Error opening temporary file." << std::endl;
        delete[] encryptedBuffer;
        return;
    }
    tempFile.write((char*)encryptedBuffer, buffer.size());
    tempFile.close();

