        delete[] encryptedBuffer;
        return;
    }

    // Securely delete the temporary file
    result = remove("temp.bin");
    if (result != 0) {
        std::cerr << "Error deleting temporary file." << std::endl;
        delete[] encryptedBuffer;
        return;
    }

    delete[] encryptedBuffer;
}

